package com.rntestmodule;

import android.content.Intent;
import android.support.annotation.Nullable;
import android.util.Log;
import android.widget.Toast;

import com.facebook.react.bridge.ActivityEventListener;
import com.facebook.react.bridge.Arguments;
import com.facebook.react.bridge.Callback;
import com.facebook.react.bridge.LifecycleEventListener;
import com.facebook.react.bridge.NativeModule;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactContext;
import com.facebook.react.bridge.ReactContextBaseJavaModule;
import com.facebook.react.bridge.ReactMethod;
import com.facebook.react.bridge.WritableMap;
import com.facebook.react.modules.core.DeviceEventManagerModule;

import java.util.HashMap;
import java.util.Map;

public class ToastModule extends ReactContextBaseJavaModule implements ActivityEventListener,
LifecycleEventListener{

    private static final String DURATION_SHORT_KEY = "SHORT";
    private static final String DURATION_LONG_KEY = "LONG";


    public ToastModule(ReactApplicationContext reactContext) {
        super(reactContext);

        reactContext.addActivityEventListener(this);
        reactContext.addLifecycleEventListener(this);
    }


    @Override
    public void onHostResume() {
        WritableMap params = Arguments.createMap();
        params.putString("values", "i am good!");

        Log.i("onResume", "hostResume");
        sendEvent(getReactApplicationContext(), "keyboardwillshow", params);
        // params = {values : "i am good!"}


    }

    @Override
    public void onHostPause() {

    }

    @Override
    public void onHostDestroy() {

    }

    //get the result here
    @Override
    public void onActivityResult(int requestCode, int resultCode, Intent data) {

    }



    @Override
    public String getName() {
        return "Toast";
    }

    @Override
    public Map<String, Object> getConstants() {
        final Map<String, Object> constants = new HashMap<>();
        constants.put(DURATION_SHORT_KEY, Toast.LENGTH_SHORT);
        constants.put(DURATION_LONG_KEY, Toast.LENGTH_LONG);
        return constants;
    }

    @ReactMethod
    public void show(String message, int duration) {
        Toast.makeText(getReactApplicationContext(), message, duration).show();
    }


    
    @ReactMethod
    public void callBackDemo(String input, Callback callback){
        input = "";
        callback.invoke(input);
    }


    //it is a good way to send something to JS part
    private void sendEvent(ReactContext reactContext,
                           String eventName,
                           @Nullable WritableMap params){
        reactContext
                .getJSModule(DeviceEventManagerModule.RCTDeviceEventEmitter.class)
                .emit(eventName, params);
    }

}